declare namespace java {
    namespace util {
        // @ts-ignore
         class ConcurrentModificationException extends java.lang.RuntimeException {
            /**
             * Constructs a ConcurrentModificationException with no
             * detail message.
             */
            // @ts-ignore
            constructor()
            /**
             * Constructs a {@code ConcurrentModificationException} with the
             * specified detail message.
             */
            // @ts-ignore
            constructor(message: string)
            /**
             * Constructs a new exception with the specified cause and a detail
             * message of {@code (cause==null ? null : cause.toString())} (which
             * typically contains the class and detail message of {@code cause}.
             */
            // @ts-ignore
            constructor(cause: java.lang.Throwable)
            /**
             * Constructs a new exception with the specified detail message and
             * cause.
             * <p>Note that the detail message associated with <code>cause</code> is
             * <i>not</i> automatically incorporated in this exception's detail
             * message.
             */
            // @ts-ignore
            constructor(message: string, cause: java.lang.Throwable)
        }
    }
}
