                    constructor(message: org.spongepowered.api.text.Text)
                    /**
                     * Create a permissions exception with a custom message and cause.
                     */
                    // @ts-ignore
                    constructor(message: org.spongepowered.api.text.Text, cause: java.lang.Throwable)
